package loops;

public class While_Loops {

	public static void main(String[] args) {
//		// TODO Auto-generated method stub
		int i=10;

		while(i<=10)	
		{
		
//			i+=1;
			System.out.println(i);
			
		}
		if(i<10)
		{
			System.out.println("i is <10");
		}
		else if(i>=10) {
			System.out.println("i is > 10");
		}
		else
		{
			System.out.println("i is =0");
		}

	}

}
